import React, { useState } from "react";
import styles from "./CodeEditor.module.css";
import Editor from "@monaco-editor/react";

interface CodeEditorProps {
  defaultComment?: string;
}

export default function CodeEditor({ defaultComment }: CodeEditorProps) {
  const [code, setCode] = useState<string | undefined>("");
  const handleCodeChange = (value: string | undefined) => {
    setCode(value);
    console.log("Value of code", value);
  };
  return (
    <div className={styles.container}>
      <div className={styles.codeEditor}>
        <Editor
          height="70vh"
          defaultLanguage="java"
          defaultValue="//Some comment"
          onChange={handleCodeChange}
          options={{
            fontFamily: "JetBrains Mono, Fira Code, monospace",
            fontSize: 14,
            lineHeight: 22,

            minimap: { enabled: false },
            scrollBeyondLastLine: false,

            padding: { top: 16, bottom: 16 },
            cursorSmoothCaretAnimation: "on",
            smoothScrolling: true,

            renderLineHighlight: "gutter",
            lineNumbersMinChars: 3,

            contextmenu: false,
            wordWrap: "on",

            scrollbar: {
              verticalScrollbarSize: 8,
              horizontalScrollbarSize: 8,
              useShadows: false,
            },
            automaticLayout: true,
          }}
          onMount={(editor, monaco) => {
            monaco.editor.defineTheme("sleek-dark", {
              base: "vs-dark",
              inherit: true,
              rules: [
                { token: "comment", foreground: "6B7280" },
                { token: "keyword", foreground: "8B5CF6" },
                { token: "string", foreground: "22C55E" },
              ],
              colors: {
                "editor.background": "#0f172a",
                "editor.lineHighlightBackground": "#020617",
                "editorCursor.foreground": "#8b5cf6",
                "editor.selectionBackground": "#1e293b",
              },
            });

            monaco.editor.setTheme("sleek-dark");
          }}
          //   onMount={(editor, monaco) => {
          //     // Disable Ctrl+Shift+P
          //     editor.addCommand(
          //       monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.KeyP,
          //       () => {}
          //     );

          //     // Disable F1
          //     editor.addCommand(monaco.KeyCode.F1, () => {});
          //   }}
        />
      </div>
    </div>
  );
}
